var context = $evaluation.getContext();
var permission = $evaluation.getPermission();
var resource = permission.getResource();
var identity = context.getIdentity();
var userAttributes = identity.getAttributes();
var userAttributesNames = userAttributes.toMap()["attributes"];
var resourceAttributes = resource.getAttributes();
var attributes = resourceAttributes.attributes;

for (var i = 0; i < attributes.length; i++) {
    var attribute = attributes.get(i);
    if (userAttributesNames && userAttributesNames.contains(attribute)) {
        $evaluation.grant();
        break;
    }
}
